import React, { useState } from 'react';
import { View, TextInput, Pressable, Alert, StyleSheet, Text, SafeAreaView } from 'react-native';
import Logo from '../Components/Logo';
import LinearGradient from '../Components/LinearGradient';
import { widthPercentageToDP as wp, heightPercentageToDP as hp } from 'react-native-responsive-screen';

const API_URL = 'http://localhost:3000/users';

const adjustFontSize = (size: number) => {
    if (screenWidth > 768) { // for screens wider than 768 pixels (typical tablet breakpoint)
        return size * 0.7; // reduce font size by 30% for larger screens
    }
    return size;
};

const LoginScreen: React.FC = () => {
    const [email, setEmail] = useState<string>('');
    const [password, setPassword] = useState<string>('');

    const handleLogin = async () => {
        try {
            const response = await fetch(`${API_URL}/login`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    email: email.toLowerCase(),
                    password,
                }),
            });

            const data = await response.json();
            console.log(data);

            if (response.status === 200) {
                await AsyncStorage.setItem('bearer', data.bearer);
                Alert.alert('Success', 'Login successful');
            } else {
                Alert.alert('Error', data.error);
            }
        } catch (error) {
            Alert.alert('Error', 'Something went wrong. Please try again.');
        }
    };

    return (
        <LinearGradient colors={['#6C5B7B', '#C06C84', '#F67280']} style={styles.container}>
            <View style={styles.logoContainer} >
                <Logo color='#FFFFFF' />
            </View>
            <Text style={styles.title}>TITS</Text>
            <Text style={styles.subtitle}>Task Integration & Technology System</Text>
            <View style={styles.inputContainer}>
                <TextInput
                    style={styles.input}
                    placeholder="Nom d'utilisateur"
                    placeholderTextColor="#FFF"
                    onChangeText={setEmail}
                    value={email}
                />
                <TextInput
                    style={styles.input}
                    placeholder="Mot de passe"
                    placeholderTextColor="#FFF"
                    secureTextEntry={true}
                    onChangeText={setPassword}
                    value={password}
                />
                {/* <Button title="Se connecter" onPress={handleLogin} color="#6C5B7B" /> */}
                <Pressable style={styles.button}>
                    <Text style={styles.buttonText}>
                        Connect
                    </Text>
                </Pressable>
            </View>
        </LinearGradient >
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'center',
        padding: 16,
    },
    logoContainer: {
        flexDirection: 'row',
        alignSelf: 'center',
        marginBottom: hp('2.5%'),
    },
    title: {
        fontSize: adjustFontSize(wp('8%')),
        fontSize: wp('8%'),
        marginBottom: hp('2.5%'),
        color: '#FFF',
        textAlign: 'center',
        fontWeight: 'bold',
    },
    subtitle: {
        fontSize: wp('5.5%'),
        marginBottom: hp('2.5%'),
        color: '#FFF',
        textAlign: 'center',
    },
    inputContainer: {
        alignSelf: 'center',
        width: wp('80%'),
        textAlign: 'center',
    },
    input: {
        height: hp('5%'),
        borderColor: '#FFF',
        borderBottomWidth: 1,
        marginBottom: hp('1.5%'),
        paddingLeft: 8,
        color: '#FFF',
        textAlign: 'center',
    },
    button: {
        backgroundColor: '#6C5B7B',
        padding: 10,
        alignItems: 'center',
        justifyContent: 'center',
        borderRadius: 5,
        width: '50%',
        flex: 1,
        alignSelf: 'center',
    },
    buttonText: {
        color: 'white',
        fontSize: wp('4%'),
    },
});


export default LoginScreen;
