import React, { useState } from 'react';
import { View, TextInput, Button, Alert, StyleSheet, Text } from 'react-native';

const LinearGradient = ({ colors, style, children }) => {
  const background = `linear-gradient(180deg, ${colors.join(', ')})`;
  return (
    <View style={{ ...style, background }}>
      {children}
    </View>
  );
};

const API_URL = 'http://localhost:3000'; // Replace with your API's base URL

const App = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async () => {
    try {
      const response = await fetch(`${API_URL}/login`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: email.toLowerCase(),
          password,
        }),
      });

      const data = await response.json();

      if (response.status === 200) {
        await AsyncStorage.setItem('bearer', data.bearer);
        Alert.alert('Success', 'Login successful');
      } else {
        Alert.alert('Error', data.error);
      }
    } catch (error) {
      Alert.alert('Error', 'Something went wrong. Please try again.');
    }
  };

  return (
    <View style={styles.container}>
      <TextInput
        style={styles.input}
        placeholder="Email"
        onChangeText={setEmail}
        value={email}
      />
      <TextInput
        style={styles.input}
        placeholder="Password"
        secureTextEntry={true}
        onChangeText={setPassword}
        value={password}
      />
      <Button title="Login" onPress={handleLogin} />
      {/* Add buttons for Register and Get All Users if needed */}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    padding: 16,
  },
  title: {
    fontSize: 24,
    marginBottom: 20,
    color: '#FFF',
    textAlign: 'center',
  },
  inputContainer: {
    alignSelf: 'center',
    width: '60%', // Adjust this value to make the input fields narrower or wider
  },
  input: {
    height: 40,
    borderColor: '#FFF',
    borderBottomWidth: 1,
    marginBottom: 12,
    paddingLeft: 8,
    color: '#FFF',
  },
});

export default App;
