import React, { useState } from 'react';
import { View, TextInput, Button, Alert, StyleSheet, Text } from 'react-native';
import Logo from './Components/Logo';

interface LinearGradientProps {
  colors: string[];
  style: object;
  children: React.ReactNode;
}

const LinearGradient: React.FC<LinearGradientProps> = ({ colors, style, children }) => {
  const background = `linear-gradient(180deg, ${colors.join(', ')})`;
  return (
    <View style={{ ...style, background }}>
      {children}
    </View>
  );
};

const API_URL = 'http://localhost:3000/users';

const App: React.FC = () => {
  const [email, setEmail] = useState<string>('');
  const [password, setPassword] = useState<string>('');

  const handleLogin = async () => {
    try {
      const response = await fetch(`${API_URL}/login`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: email.toLowerCase(),
          password,
        }),
      });

      const data = await response.json();
      console.log(data);

      if (response.status === 200) {
        await AsyncStorage.setItem('bearer', data.bearer);
        Alert.alert('Success', 'Login successful');
      } else {
        Alert.alert('Error', data.error);
      }
    } catch (error) {
      Alert.alert('Error', 'Something went wrong. Please try again.');
    }
  };

  return (
    <LinearGradient colors={['#6C5B7B', '#C06C84', '#F67280']} style={styles.container}>
      <View style={styles.logoContainer} >
        <Logo color='#FFFFFF' />
      </View>
      <Text style={styles.title}>TITS</Text>
      <Text style={styles.subtitle}>Task Integration & Technology System</Text>
      <View style={styles.inputContainer}>
        <TextInput
          style={styles.input}
          placeholder="Nom d'utilisateur"
          placeholderTextColor="#FFF"
          onChangeText={setEmail}
          value={email}
        />
        <TextInput
          style={styles.input}
          placeholder="Mot de passe"
          placeholderTextColor="#FFF"
          secureTextEntry={true}
          onChangeText={setPassword}
          value={password}
        />
        <Button title="Se connecter" onPress={handleLogin} color="#6C5B7B" />
      </View>
    </LinearGradient>
  );
};

const styles = StyleSheet.create({
  logoContainer: {
    flexDirection: 'row',
    alignSelf: 'center',
    marginBottom: 20,
  },
  container: {
    flex: 1,
    justifyContent: 'center',
    padding: 16,
  },
  title: {
    fontSize: 30,
    marginBottom: 20,
    color: '#FFF',
    textAlign: 'center',
    fontWeight: '500',
  },
  subtitle: {
    fontSize: 22,
    marginBottom: 20,
    color: '#FFF',
    textAlign: 'center',
    fontWeight: '500',
  },
  inputContainer: {
    alignSelf: 'center',
    width: '60%', // Adjust this value to make the input fields narrower or wider
  },
  input: {
    height: 40,
    borderColor: '#FFF',
    borderBottomWidth: 1,
    marginBottom: 12,
    paddingLeft: 8,
    color: '#FFF',
  },
});

export default App;
